import React from "react";
import "./index.css";
import Header from "./components/Header/Header";
import Buttons from "./components/Buttons/Buttons";
import Status from "./components/Status/Status";
import Users from "./components/Users/Users";
import Spinner from './components/Spinner/Spinner'
import AddTaskForm from "./components/AddTaskForm/AddTaskForm";
import tasks from "./utils/tasks.json";
import usersdata from "./utils/users.json";
import statusesdata from "./utils/statuses.json";
import _ from "lodash";
import TaskManagerService from './services/TaskManagerService';

const API = new TaskManagerService();

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      tasks:[],
      users: [],
      statuses:[],
      term: "",
      contractorFilter: "",
      statusFilter: "",
      valueUser: "",
      valueStatus: "",
      loading:true
    };
  }

  doClick(arr) {
    this.setState({ tasks: arr }, () => {
      localStorage.setItem("tasks", JSON.stringify(arr));
    });
  }

  search = (term, arr) => {
    if (term === "" || term == null) {
      return arr;
    }
    return arr.filter((i) =>
      i.title.toLowerCase().includes(term.toLowerCase())
    );
  };

  setTermSeacrh = (term) => {
    this.setState({ term: term });
  };

  filter = (flagUser, flagStatus, valueUser, valueStatus, arr) => {
    if (flagStatus !== "" || flagUser !== "") {
      if (flagUser === "user") {
        arr = arr.filter((i) => i.contractor_id === +valueUser);
      }
      if (flagStatus === "status") {
        arr = arr.filter((i) => i.status === +valueStatus);
      }
    } else {
      return arr;
    }

    return arr;
  };

  setContractorFilter = (flagContractor, value) => {
    if (value === "all") {
      this.setState(() => {
        return { contractorFilter: "", valueUser: value };
      });
    } else {
      this.setState(() => {
        return { contractorFilter: flagContractor, valueUser: value };
      });
    }
  };

  setStatusFilter = (flagStatus, value) => {
    if (value === "all") {
      this.setState(() => {
        return { statusFilter: "", valueStatus: value };
      });
    } else {
      this.setState(() => {
        return { statusFilter: flagStatus, valueStatus: value };
      });
    }
  };

  addTask = (newItem) => {
    let newTasks = _.cloneDeep(this.state.tasks);
    newTasks.push(newItem);
    this.setState(
      () => {
        return { tasks: newTasks };
      },
      () => {
        localStorage.setItem("tasks", JSON.stringify(newTasks));
      }
    );
  };

  clear() {
    localStorage.clear();
  }

  async componentDidMount() {
    await API.getUsers().then((res)=>{
      this.setState(()=> { return {users:res.data}})
    })

    await API.getStatuses().then((res)=>{
      this.setState(()=> { return {statuses:res.data}})
    })
    await API.getTasks().then((res)=>{
      this.setState(()=> { return {tasks:res.data, loading:false}})
    })

    if (localStorage.tasks) {
      this.setState({ tasks: JSON.parse(localStorage.getItem("tasks")) });
    }
    
  }

  render() {
    const {
      term,
      contractorFilter,
      statusFilter,
      valueUser,
      valueStatus,
      tasks,
      loading
    } = this.state;
    const visibleTasks = this.filter(
      contractorFilter,
      statusFilter,
      valueUser,
      valueStatus,
      this.search(term, tasks)
    );

    if(loading) {return( <Spinner/>)}

