/* eslint-disable array-callback-return */
import React from "react";
import _ from "lodash";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import {did, canceled, closed} from '../../redux/actions'

const Buttons = ({ tasks, item, doClick },props) => {

  const mapDispatchToProps = (dispatch) => {
    return bindActionCreators(
      {
        did,
        canceled,
        closed
      },
      dispatch
    );
  };
  const {did, canceled, closed}=props

  function handleClick(btn, action) {
    let arr = _.cloneDeep(tasks);

    arr.map((i) => {
      if (i.id === btn.id) {
        return (i.status = action);
      }
    });
    doClick(arr);
  }

  if (item.status === 1) {
    return (
      <td id="buttons">
        <input
          type="button"
          id="btnDo"
          onClick={()=>did()}
          className="btn btn-primary btn-sm"
          value="Выполнить"
        />
        <input
          type="button"
          id="btnCancel"
          onClick={()=>canceled()}
          className="btn btn-warning btn-sm"
          value="Отменить"
        />
      </td>
    );
  } else if (item.status === 2) {
    return (
      <td id="buttons">
        <input
          type="button"
          id="btnClose"
          onClick={()=>closed()}
          className="btn btn-success btn-sm"
          value="Закрыть"
        ></input>
      </td>
    );
  } else {
    return <td id="buttons"></td>;
  }
};

export default connect( Buttons.mapDispatchToProps)
  (Buttons);
